mapper.select:
select

from prd_customer
where status not in(-1,2)

	and customer_name = #{customerName,jdbcType=VARCHAR}


	and customer_type = #{customerType,jdbcType=VARCHAR}


	and telephonenumber = #{telephonenumber,jdbcType=VARCHAR}



		and city = #{city,jdbcType=VARCHAR}


		and city not in

			#{item}




	and house_status = #{houseStatus,jdbcType=CHAR}


	and level = #{level,jdbcType=CHAR}


	and customer_source = #{customerSource,jdbcType=VARCHAR}


	and communit_time = #{communitTime,jdbcType=TIMESTAMP}


	and communit_time is not null





	and date(last_update_time) >= #{createTimeBegin} and date(last_update_time) <= #{createTimeEnd}




	and date(last_update_time) <= #{createTimeBegin}





	and status = #{status,jdbcType=INTEGER}




			and (loan_amount  > 0 and  loan_amount <= 20)


			and (loan_amount  > 20 and  loan_amount <= 50)



			and (loan_amount  > 50 and  loan_amount <= 100)


			and (loan_amount  > 100 and  loan_amount <= 500)


			and (loan_amount  > 500 )



and is_deleted=0
order BY  last_update_time DESC
limit ${start},${size}

select count(*)
from prd_customer
where status not in(-1,2)

	and customer_name = #{customerName,jdbcType=VARCHAR}


	and customer_type = #{customerType,jdbcType=VARCHAR}


	and telephonenumber = #{telephonenumber,jdbcType=VARCHAR}



		and city = #{city,jdbcType=VARCHAR}


		and city not in

			#{item}




	and house_status = #{houseStatus,jdbcType=CHAR}


	and level = #{level,jdbcType=CHAR}


	and customer_source = #{customerSource,jdbcType=VARCHAR}


	and communit_time = #{communitTime,jdbcType=TIMESTAMP}


	and communit_time is not null





				and date(last_update_time) >= #{createTimeBegin} and date(last_update_time) <= #{createTimeEnd}




				and date(last_update_time) <= #{createTimeBegin}





	and status = #{status,jdbcType=INTEGER}




			and (loan_amount  > 0 and  loan_amount <= 20)


			and (loan_amount  > 20 and  loan_amount <= 50)



			and (loan_amount  > 50 and  loan_amount <= 100)


			and (loan_amount  > 100 and  loan_amount <= 500)


			and (loan_amount  > 500 )



and is_deleted=0
